INSERT INTO `xdh_teachers` VALUES ('2', '教学总监  <br> 高洛峰', '寓教于乐 学习编程不再枯燥', '敢于技术高难度挑战。近年致力于国内LAMP技术推广和研发，目标桃李满天下，每个互联网公司都有我的学员，每个PHP开发者都用《细说PHP》。', '/Home/images/glf.jpg', '2016-01-06', '2016-01-06', null);
INSERT INTO `xdh_teachers` VALUES ('3', '创始人    <br>李明', '勤奋好学 天道酬勤', '70后北漂老男孩，做过程序员，干过运维，跑过市场，当过讲师，LAMP兄弟连副校长，Linux 狂热分子，PHP技术推动者，致力于开源技术推广，坚信授之以鱼不如授之以渔，曲径通幽天道酬勤。', '/Home/images/lm.jpg', '2016-01-06', '2016-01-06', null);

